const assert = require('chai').assert;
const Stream = require('../src/index.js');

describe('Receive older data', () => {
    it('should receive the last data', () => {
        let add = 0;
        const b = new Stream();
        b.notify(2);
        b.then(data => {
            add = data;
        });
        assert.equal(add, 2);
    });

    it('it shouldn\'t run when no data is sent', () => {
        let test = false;
        const b = new Stream();
        b.then(() => {
            test = true;
        });
        assert.equal(test, false);
    });
});